import React from 'react'
const tasks = [

    {
        id: 1,
        text: 'Doctors Appointment',
        day: 'FEB 5th at 2:30pm',
        reminder: 'true',
    },

    
    {
        id: 2,
        text: 'Meeting at School',
        day: 'FEB 6th at 1:30pm',
        reminder: 'true',
    },

    
    {
        id: 3,
        text: 'Food Shopping',
        day: 'FEB 5th at 2:30pm',
        reminder: 'false',
    }
]
const Tasks = () => {
  return (
    <>
      {tasks.map((task) => (<h3>{task.text}</h3>))} 
    </>
  )
}

export default Tasks
